mapper.select:
SELECT auth_name
FROM emergency_auth
WHERE role_name = #{role}

SELECT user_id AS userName, user_name AS nickName, password, role_name AS role,enabled
FROM NUSER n
WHERE n.user_id = #{userName}

SELECT COUNT(1)
FROM NUSER
WHERE user_id = #{userName}

mapper.update:
UPDATE NUSER
SET password = #{password}
WHERE user_id = #{userName}

mapper.select:
SELECT n.user_id AS userName, n.user_name AS nickName, CASE r.role_name WHEN 'ADMIN' THEN '管理员' WHEN 'APPROVER' THEN '审核员' WHEN 'OPERATOR' THEN '操作员' END AS role, last_modified_date AS updateTime, CASE enabled WHEN 'T' THEN '正常' WHEN 'F' THEN '失效' END AS enabled FROM NUSER n,emergency_role r
n.role_name!='SUPER_USER' AND n.role_name!='SYSTEM_USER' AND n.user_id = r.user_name
AND n.user_id LIKE CONCAT('%',#{userName},'%')
AND n.user_name LIKE CONCAT('%',#{nickName},'%')
AND r.role_name = #{role}
AND n.enabled = #{enabled}

mapper.update:
UPDATE NUSER
SET enabled = #{enable}
WHERE user_id IN

    #{userName}

UPDATE NUSER
SET password = #{password}
WHERE user_id = #{userName}

UPDATE NUSER
SET user_name = #{nickName},role_name = #{role},last_modified_date=#{updateTime}
WHERE user_id = #{userName}

mapper.select:
SELECT role_name
FROM emergency_role
WHERE user_name = #{userName}
