mapper.select:
SELECT auth_name
FROM emergency_auth
WHERE role_name = #{role}

SELECT user_name, nick_name, password, role_name AS role,enabled
FROM emergency_user
WHERE user_name = #{userName}

SELECT COUNT(1)
FROM emergency_user
WHERE user_name = #{userName}

mapper.update:
UPDATE emergency_user
SET password = #{password},last_modified_date=#{timestamp}
WHERE user_name = #{userName}

mapper.select:
SELECT user_name, nick_name, CASE role_name WHEN 'ADMIN' THEN '管理员' WHEN 'APPROVER' THEN '审核员' WHEN 'OPERATOR' THEN '操作员' END AS role, last_modified_date AS updateTime, CASE enabled WHEN 'T' THEN '正常' WHEN 'F' THEN '失效' END AS enabled FROM emergency_user
AND user_name LIKE CONCAT('%',#{userName},'%')
AND nick_name LIKE CONCAT('%',#{nickName},'%')
AND role_name = #{role}
AND enabled = #{enabled}

mapper.update:
UPDATE emergency_user
SET enabled = #{enable},last_modified_date=#{timestamp}
WHERE user_name IN

    #{userName}

UPDATE emergency_user
SET user_name = #{nickName},role_name = #{role},last_modified_date=#{updateTime}
WHERE user_name = #{userName}
